import axios from "axios";

class AuthenticatorService {

    // Creo uno stet con user e psw hardcoded (per ora)
    state = {
        user: "admin",
        psw: "admin"
    }

    // Metodo di salvataggio
    setUserInformations = (username) => {
        // Salvo nel session storage [Parametri: nome(str), params(funzione)]
        sessionStorage.setItem("user", username);
    }

    // Metodo di rimozione info utente nella session
    clearUserInformations = () => {
        sessionStorage.removeItem("user");
    }

    // Ottenere info utente
    getUserInformations = () => {
        return sessionStorage.getItem("user");
    }

    // BOOL: controllo se l'utente Ã¨ loggato
    isLoggedIn = () => {
        let user = this.getUserInformations();
        // Strict check
        if (user === null) return false;
        else return true;
    }

    // Creo un metodo di axios (interceptor) che genera un 
    // BasicAuthHeader (come commentato "old" hard-coded nelle API)
    setupAxiosInterceptors() {
        let BasicAuthHeader = "Basic " + window.btoa(this.state.user + ":" + this.state.psw);
        axios.interceptors.request.use(
            (config) => {
                if (this.isLoggedIn()) {
                    config.headers.authorization = BasicAuthHeader
                }
                return config;
            }
        )
    }
}

export default new AuthenticatorService();
